*/

USE joebase;

----------------------------------------------------------------------------------------------------
-- DELETE SECTION
-- Ensure the table is clean before (re)initializing.
----------------------------------------------------------------------------------------------------

DELETE FROM series;
GO

----------------------------------------------------------------------------------------------------
-- DATA INITIALIZATION SECTION
-- Values were generated by running a complex concatenation function on the unique set of
-- manufacturer/series combinations in the original Joebase Excel spreadsheet.
----------------------------------------------------------------------------------------------------


----------------------------------------------------------------------------------------------------
-- POST-LOAD VERIFICATION SECTION
-- Select the raw table data and then also do a joined select to get something more easily
-- human-verifiable where the manfucturer's name, not just identifier is output.
----------------------------------------------------------------------------------------------------

SELECT * from series;
GO

SELECT m.manufacturer_name, s.series_description, s.series_name
FROM series s
JOIN manufacturer m ON m.manufacturer_id = s.manufacturer_id;
GO